mapper.select:
select
  id, user_id, book_id, amount, create_time, state, return_time
from t_borrow_return


        and id = #{id}


        and user_id = #{userId}


        and book_id = #{bookId}


        and amount = #{amount}


        and create_time = #{createTime}


        and state = #{state}


        and return_time = #{returnTime}


limit #{start}, #{limit}

select
count(*)
from t_borrow_return


        and id = #{id}


        and user_id = #{userId}


        and book_id = #{bookId}


        and amount = #{amount}


        and create_time = #{createTime}


        and state = #{state}


        and return_time = #{returnTime}
